import React, {useEffect, useState} from 'react';
import './App.css';

function App() {
  const [data, setData] = useState([])
  const [dogFocus, setDogFocus] = useState([])
  useEffect(() => {
    fetch(`http://localhost:3001/pups`)
    .then(resp => resp.json())
    .then(data => {
      console.log('Data: ', data)
      setData(data)
    })
  }, [])
  const changeDogFocus = (pup) => {
    setDogFocus(pup)
  }
  const renderPups = (pup) => {
    return <span onClick={changeDogFocus(pup)}>{pup.name}</span>
  }
  return (
    <div className="App">
      <div id="filter-div">
        <button id="good-dog-filter">Filter good dogs: OFF</button>
      </div>
      <div id="dog-bar">
        {data.map((each, index) => renderPups(each))}
      </div>
      <div id="dog-summary-container">
        <h1>DOGGO:</h1>
        <div id="dog-info">
          {dogFocus.length == 1 ? <div>
            <img src={dogFocus.image} alt={dogFocus.name} />
            <h2>{dogFocus.name}</h2>
            <button>{dogFocus.isGoodDog ? "Good Dog!": "Bad Dog!"}</button>
            </div> : null}

        </div>
      </div>
    </div>
  );
}

export default App;
